// v1.0.0 - Haydn Williams - original
// v1.0.1 - Haydn Williams - refactor to avoid bug using TOP 1 Composition
// v1.0.2 - Haydn Williams - Increased default last_x_days to 365 and added global parameter

var ehrIDReplace = "<ehrid>";

var replaceMe = "<replace_date>";
var cuid = "<replace_cuid>";
var formName = "Moving and Handling Assessment and Care Plan";
var LASTXDAYS = 365;

var getRecentCompositionsAQL = "select DISTINCT e/ehr_id/value as ehr_id " +
	"FROM EHR e  " +
	"CONTAINS COMPOSITION c[openEHR-EHR-COMPOSITION.encounter.v1]   " +
	"CONTAINS EVALUATION f[openEHR-EHR-EVALUATION.patient_handling.v0]  " +
	"WHERE c/name/value = '" + formName + "' " + 
	"AND c/context/start_time/value > '" + replaceMe + "' " +
	"ORDER BY c/context/start_time/value DESC " + 
	"LIMIT 1000000000";
	
var getLatestFormDataAQL = "SELECT c/uid/value as cuid, " +
       "c/name/value, " +
       "c/context/start_time/value as completed, " +
       "e/ehr_status/subject/external_ref/id/value as mrn, " +
       "f/data[at0001]/items[at0015]/value/value as risk " +
	"FROM EHR e  " +
	"CONTAINS COMPOSITION c[openEHR-EHR-COMPOSITION.encounter.v1]   " +
	"CONTAINS EVALUATION f[openEHR-EHR-EVALUATION.patient_handling.v0]  " +
	"WHERE c/name/value = '" + formName + "' " + 
	"AND e/ehr_id/value = '" + ehrIDReplace + "' " +
	"ORDER BY c/context/start_time/value DESC " + 
	"LIMIT 1";
	
function generateEhrQuery(aql, params) {
    return Ehr.query({
        aql: aql,
        initvalue: [],
        params: params,
        callback: function(out, result) {
            out.push(result);
        }
    });
}	
	
function getFromDate(ctx) {
	var x = LASTXDAYS;
	if(ctx && ctx.vars){
		x = ctx.vars.last_x_days;
		if(!x || x == "") {
			x = ctx.vars.last_x_days = LASTXDAYS;
		}
	}
	
	var oldDate = null;
	var d = new Date();
	oldDate = new Date(d.setDate(d.getDate() - x));
	return oldDate.toISOString();
}

function getLatestComposition(ehr_id) {
	var forms = {};
	var promises = {
		composition: generateEhrQuery(
			getLatestFormDataAQL.replace(ehrIDReplace, ehr_id), 
			{}
		)
	};
	Ehr.allhash(promises, function (res) {
		var form = res.composition[0];
		forms = {
			"form_name": formName,
			"form_id": form.cuid,
			"recorded_date": form.completed,
			"patient_mrn": form.mrn,
			"risk": form.risk
		}
	});
	return forms;
}
	
function getRecentCompositions(ctx) {
	var fromDate = ''
	var dateException = null;
	
	try {
		fromDate = ctx.vars.from_date = getFromDate(ctx);
	} catch(ex) {
		dateException = ex;
	}
	
	var query = getRecentCompositionsAQL.replace(replaceMe, fromDate);

	var forms = [];
	var promises = {
		compositions: generateEhrQuery(query, {})
	};
	
	try {
		Ehr.allhash(promises, function (res) {
			for(var i = 0; i < res.compositions.length; i++) {
				if(res.compositions[i].ehr_id) {
					try {
						var comp = getLatestComposition(res.compositions[i].ehr_id);
						if(comp) {
							forms.push(comp);
						}
					} catch(ex) {
						forms.push({
							message: 'Exception in second call',
							ehr_id: res.compositions[i].ehr_id,
							exception: ex.message
						});
					}
					
				}
			}
		});
		
		var parseISOString = function(s) {
		  var b = s.split(/\D+/);
		  return new Date(Date.UTC(b[0], --b[1], b[2], b[3], b[4], b[5], b[6]));
		};
		
		forms.sort(function(a,b) {			
			var da = parseISOString(a.recorded_date).getTime();
			var db = parseISOString(b.recorded_date).getTime();
			
			return db-da; // reverse sort
		});
		
	} catch(ex) {
		forms.push({
			message: 'Exception in first call',
			date_from: fromDate,
			query: query,
			exception: ex.message
		});
	}
	
	return forms;
	
}

function compute(ctx, src) {
	return getRecentCompositions(ctx);
}
