steps.processorData:
var fromDate = "<replace_date>";
var formName = "Multidisciplinary individualised falls care plan";

var only_completed = "SELECT c/uid/value as cuid, " +
       "c/name/value, " +
       "c/context/start_time/value as completed, " +
       "e/ehr_status/subject/external_ref/id/value as mrn, " +
       "z/data[at0002]/items[at0005]/items[at0006]/value/value as over_65, " +
       "z/data[at0002]/items[at0005]/items[at0007]/value/value as last_year, " +
       "z/data[at0002]/items[at0005]/items[at0008]/value/value as confused, " +
       "z/data[at0002]/items[at0005]/items[at0009]/value/value as belief " +
"FROM EHR e  " +
"CONTAINS TOP 1 COMPOSITION c[openEHR-EHR-COMPOSITION.encounter.v1] " +
"CONTAINS EVALUATION z[openEHR-EHR-EVALUATION.patient_falls_risk.v0]  " +
"WHERE c/name/value = '" + formName + "' " + 
"AND completed > '" + fromDate + "' " +
"ORDER BY c/context/start_time/value DESC " +
"LIMIT 1000;";

function compute(ctx, src) {
	var x = ctx.vars.last_x_days;
	if(!x || x == "") {
		x = ctx.vars.last_x_days = 30;
	}

	var d = new Date();
	var theLongLongAgo = new Date(d.setDate(d.getDate() - x));
	ctx.vars.from_date = theLongLongAgo.toISOString();
	
	var query = only_completed.replace(fromDate, ctx.vars.from_date);
	
	var promises = {
        forms: Ehr.query({
			aql: query,
			params: ctx.vars,
			initvalue: [],
			callback: function (out, form) {
				out.push({
						"form_name": formName,
						"form_id": form.cuid,
						"recorded_date": form.completed,
						"patient_mrn": form.mrn,
						"risks": {
							"over_65": form.over_65,
							"fallen_in_last_year": form.last_year,
							"believe_at_risk": form.belief,
							"confused": form.confused,
							"overall_risk": form.over_65 || form.last_year || form.belief || form.confused 
						}
				});
			}
		})
	};

    var result = {};
	try {
		Ehr.allhash(promises, function (res) {
			result = res['forms'];
			result.params = res['params'];
		});
	} catch(ex) {
		result = {
			exception: ex
		};
	}
    
    return result;
}
