// DEPRECATED! see PBIDashboards::DiabeticFoot

var fromDate = "<replace_date>";
var formName = "Diabetic Foot Screening";

var only_completed = "SELECT c/uid/value as cuid, " +
       "c/name/value, " +
       "c/context/start_time/value as completed, " +
       "e/ehr_status/subject/external_ref/id/value as mrn, " +
       "j/data[at0001]/items[at0027]/items[at0036]/value/value as referral_specialist, " +
       "j/data[at0001]/items[at0027]/items[at0021]/value/value as referral_inpatient " +
"FROM EHR e  " +
"CONTAINS TOP 1 COMPOSITION c[openEHR-EHR-COMPOSITION.encounter.v1] " +
"CONTAINS EVALUATION j[openEHR-EHR-EVALUATION.paitent_diabetes_foot_screening.v0]  " +
"WHERE c/name/value = '" + formName + "' " + 
"AND completed > '" + fromDate + "' " +
"ORDER BY c/context/start_time/value DESC  " +
"LIMIT 1000;"

function compute(ctx, src) {
	var x = ctx.vars.last_x_days;
	if(!x || x == "") {
		x = ctx.vars.last_x_days = 30;
	}

	var d = new Date();
	var theLongLongAgo = new Date(d.setDate(d.getDate() - x));
	ctx.vars.from_date = theLongLongAgo.toISOString();
	
	var query = only_completed.replace(fromDate, ctx.vars.from_date);
	
	var promises = {
        forms: Ehr.query({
			aql: query,
			params: ctx.vars,
			initvalue: [],
			callback: function (out, form) {
				out.push({
						"form_name": formName,
						"form_id": form.cuid,
						"recorded_date": form.completed,
						"patient_mrn": form.mrn,
						"referrals": {
							"inpatient": form.referral_inpatient,
							"specialist": form.referral_specialist
						}
				});
			}
		})
	};

    var result = {};
	try {
		Ehr.allhash(promises, function (res) {
			result = res['forms'];
			result.params = res['params'];
		});
	} catch(ex) {
		result = {
			exception: ex
		};
	}
    
    return result;
}
